using Buildings;using Buildings.Interfaces;using UnityEngine;[RequireComponent(typeof(Renderer), typeof(BoxCollider))]public class BuildingObjectView : MonoBehaviour, IBuildingObjectView, IRemovable{    // UNDONE Class is not finish        [SerializeField] private Renderer _renderer;    [SerializeField] private BoxCollider _boxCollider;    private IBuildingObjectPresenter _buildingPresenter;    public void SetPresenter(IBuildingObjectPresenter buildingPresenter)    {        _buildingPresenter = buildingPresenter;    }    public GameObject GetBuilding()    {        return this.gameObject;    }        public void SetPosition(Vector3 position)    {        transform.position = position;    }    public void SetColor(bool result)    {        if (result)            _renderer.material.color = Color.green;        _renderer.material.color = Color.red;    }    public void DestroyObject()    {        Destroy(this.gameObject);    }}