using UnityEngine;using Zenject;public class MouseHandler : ITickable{     readonly MouseState _mouseState;    public MouseHandler(MouseState mouseState)    {        _mouseState = mouseState;    }    public void Tick()    {        _mouseState.MousePosition = Input.mousePosition;        _mouseState.AxisX = Input.GetAxis("Mouse X");        _mouseState.AxisY = Input.GetAxis("Mouse Y");        _mouseState.IsLeftClick = Input.GetMouseButtonDown(0);        _mouseState.IsRightClick = Input.GetMouseButton(1);    }}